import React, { useEffect, useState } from 'react';
import './App.css';
import logo from './CommunityDealsLogo.JPG';
import DealCard from './DealCard';
import dealsData from './test.json';

function App() {
  const [deals, setDeals] = useState([]);

  useEffect(() => {
    setDeals(dealsData);
  }, []);

  return (
    <div>
      <header className="header">
        <div className="logo">
          <img src={logo} alt="Logo" />
        </div>
        <nav className="nav">
          <ul className="nav-list">
            <li><a href="#">Home</a></li>
            <li><a href="#">Deals</a></li>
            <li><a href="#">Categories</a></li>
            <li><a href="#">About</a></li>
            <li><a href="#">Contact</a></li>
          </ul>
        </nav>
      </header>

      <section id="featured-deals" className="featured-deals">
        <div className="section-header">
          <h2 className="section-title">Today's Hottest Deals ðŸ”¥ </h2>
        </div>
        <div className="deal-container">
          {deals.map((deal, index) => (
            <DealCard
              key={index}
              image={deal.Image}
              title={deal.Name}
              url={deal.Url}
            />
          ))}
        </div>
      </section>

      <footer className="footer">
        <p className="footer-text">&copy; 2023 Community Deal Alerts. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
