import React, { useEffect, useState } from 'react';
import './App.css';
import logo from './CommunityDealsLogo.JPG';
import DealCard from './component/DealCard';
import dealsData from './test.json';
import Pagination from './component/Pagination'
import { paginate } from './utils/paginate';


function App() {
  const [deals, setDeals] = useState([]);
  const [count, setCount] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 7;

  const paginate_deals = paginate(deals, currentPage, pageSize);


  useEffect(() => {
    setDeals(dealsData);
    setCount(dealsData.length);

  }, []);

  const handlePageChange = (page) => {
      setCurrentPage(page);
  };

  return (
    <div>
      <header className="header">
        <div className="logo">
          <img src={logo} alt="Logo" />
        </div>
        <nav className="nav">
          <ul className="nav-list">
            <li><a href="#">Home</a></li>
            <li><a href="#">Deals</a></li>
            <li><a href="#">Categories</a></li>
            <li><a href="#">About</a></li>
            <li><a href="#">Contact</a></li>
          </ul>
        </nav>
      </header>

      <section id="featured-deals" className="featured-deals">
        <div className="section-header">
          <h2 className="section-title">Today's Hottest Deals ðŸ”¥ </h2>
        </div>
        <div className="deal-container">
          {paginate_deals.map((deal, index) => (
            <DealCard
              key={index}
              image={deal.Image}
              title={deal.Name}
              url={deal.Url}
            />
          ))}
        </div>
        <div className="d-flex justify-content-center">
          <Pagination itemsCount={count} pageSize = {pageSize} onPageChange={handlePageChange} currentPage ={currentPage}/>
        </div>
      </section>


      <footer className="footer">
        <p className="footer-text">&copy; 2023 Community Deal Alerts. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
